import React, { useState } from "react";
import "./newNote.css";
import { v4 as uuidv4 } from "uuid";
import { BiSave } from "react-icons/bi";
import { IoIosColorPalette } from "react-icons/io";
import { BsFillCircleFill } from "react-icons/bs";
import { Dropdown, FormGroup } from "react-bootstrap";

const NewNote = (props) => {
  const [input, setInput] = useState("");

  const textInput = (e) => {
    setInput(e.target.value);
  };

  const textSubmit = (e) => {
    e.preventDefault();

    const newTask = {
      id: uuidv4(),
      text: input,
      deleted: false,
    };
    props.onSubmit(newTask);
    document.getElementById("inputNote").value = "";
  };

  const [color, setColor] = useState(1);

  const changeColor = (e) => {
    setColor(e.target.value);
    console.log("hola");
  };

  return (
    <form onSubmit={textSubmit}>
      <div id="newNote" className="note noteList">
        <input
          id="inputNote"
          onChange={textInput}
          className="addNote-text-area "
          placeholder="Add a new task.."
        ></input>
        <div className="dropdown">
          <div className="note-content">
            <div className="containter-buttons">
              <button className="button-newnote">
                {" "}
                <BiSave />
              </button>
              <div className="button">
                <FormGroup>
                  <Dropdown className="button newNote">
                    <Dropdown.Toggle variant="none">
                      <IoIosColorPalette />
                    </Dropdown.Toggle>

                    <Dropdown.Menu className="dropdown-target">
                      <input
                        name="group1"
                        type="radio"
                        value={"1"}
                        id="radio-1"
                        checked={color === 1 ? true : false}
                        onChange={changeColor}
                      />
                      <label for="radio-1">
                        <Dropdown.Item>
                          <FormGroup>
                            <BsFillCircleFill id="red" className="circle red" />
                          </FormGroup>
                        </Dropdown.Item>
                      </label>
                      <p className="drop-text"> In progress</p>
                      <Dropdown.Item>
                        <FormGroup>
                          <input
                            name="group1"
                            type="radio"
                            value={"2"}
                            id="radio-2"
                            checked={color === 2 ? true : false}
                            onChange={changeColor}
                          />
                          <label for="radio-2">
                            <BsFillCircleFill
                              id="yellow"
                              className="circle yellow"
                            />
                          </label>
                        </FormGroup>
                        <p className="drop-text"> Not started</p>
                      </Dropdown.Item>
                      <Dropdown.Item>
                        <FormGroup>
                          <input
                            name="group1"
                            type="radio"
                            value={"3"}
                            id="radio-3"
                            checked={color === 3 ? true : false}
                            onChange={changeColor}
                          />
                          <label for="radio-1">
                            <BsFillCircleFill
                              id="green"
                              className="circle green"
                            />
                          </label>
                        </FormGroup>
                        <p className="drop-text"> Completed</p>
                      </Dropdown.Item>
                    </Dropdown.Menu>
                  </Dropdown>
                </FormGroup>
              </div>
            </div>
          </div>
        </div>
      </div>
    </form>
  );
};

export default NewNote;
