import React, { useState } from "react";
import "./note.css";
import { BiSave } from "react-icons/bi";
import { IoIosColorPalette } from "react-icons/io";
import { BsFillTrashFill, BsFillCircleFill } from "react-icons/bs";
import { FaTrashRestore } from "react-icons/fa";
import { Dropdown } from "react-bootstrap";

const Note = ({
  text,
  id,
  date,
  deleteNote,
  deletePerm,
  deleted,
  className,
  color,
  editNote,
}) => {
  const [input, setInput] = useState(text);

  const changeColor = (e) => {
    setInput(e.target.value);

    const editNote = {
      id: id,
      text: input,
      color: color,
    };
  };

  return (
    <div className={`note ${color} ${className}`}>
      <textarea
        className={`note-text-area  ${color}`}
        defaultValue={text}
        onChange={changeColor}
      />
      <div className="note-content">
        <div className="note-content">
          <div className="containter-buttons">
            <button className={`button  ${color}`}>
              {" "}
              <BiSave />
            </button>
            {deleted ? (
              <>
                <button
                  onClick={() => deleteNote(id)}
                  className={`button  ${color} `}
                >
                  {" "}
                  <FaTrashRestore className={`button  ${color}`} />
                </button>
                <button
                  onClick={() => deletePerm(id)}
                  className={`button  ${color}`}
                >
                  {" "}
                  <BsFillTrashFill />
                </button>
              </>
            ) : (
              <button
                onClick={() => deleteNote(id)}
                className={`button  ${color}`}
              >
                {" "}
                <BsFillTrashFill />
              </button>
            )}
            <Dropdown className={`button  ${color}`}>
              <Dropdown.Toggle variant="none">
                <IoIosColorPalette />
              </Dropdown.Toggle>

              <Dropdown.Menu className="dropdown-target">
                <Dropdown.Item>
                  <BsFillCircleFill id="yellow" className="circle yellow" />
                  <p className="drop-text"> In progress</p>
                </Dropdown.Item>
                <Dropdown.Item>
                  <BsFillCircleFill id="red" className="circle red" />
                  <p className="drop-text"> Not started</p>
                </Dropdown.Item>
                <Dropdown.Item>
                  <BsFillCircleFill id="green" className="circle green" />
                  <p className="drop-text"> Completed</p>
                </Dropdown.Item>
              </Dropdown.Menu>
            </Dropdown>
            <small className="date">{date}</small>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Note;
