import React from "react";
import "./note.css";
import { BiSave } from "react-icons/bi";
import { IoIosColorPalette } from "react-icons/io";
import { BsFillTrashFill } from "react-icons/bs";
import { FaTrashRestore } from "react-icons/fa";
import { Dropdown, Menu, Item } from "bootstrap";

const Note = ({ text, id, date, deleteNote, deleted, className }) => {
  return (
    <div className={className}>
      <textarea className="note-text-area" defaultValue={text} />
      <div className="note-content">
        <small>{date}</small>
        <div className="note-content">
          <div className="containter">
            <button className="button">
              {" "}
              <BiSave />
            </button>
            <IoIosColorPalette className="color-button" />
            <DropdownButton id="dropdown-basic-button" title="Dropdown button">
              <Dropdown.Item href="#/action-1">Action</Dropdown.Item>
              <Dropdown.Item href="#/action-2">Another action</Dropdown.Item>
              <Dropdown.Item href="#/action-3">Something else</Dropdown.Item>
            </DropdownButton>
            {/* <button className="button">
              {" "}
            </button> */}
            {deleted ? (
              <button onClick={() => deleteNote(id)} className="button">
                {" "}
                <FaTrashRestore className="color-button" />
              </button>
            ) : (
              <button onClick={() => deleteNote(id)} className="button">
                {" "}
                <BsFillTrashFill />
              </button>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Note;
